import React, { useEffect, useState } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';
import axios from 'axios';

const LoanChart = ({ extraPayment }) => {
    const [data, setData] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const result = await axios.post('http://127.0.0.1:5000/schedule', {
                    balance: 300000,  // example values
                    interest_rate: 3.5,
                    monthly_payment: 1500,
                    term: 360
                });
                setData(result.data);
            } catch (err) {
                console.error(err);
                setError('Failed to fetch data from server');
            }
        };
        fetchData();
    }, []);

    useEffect(() => {
        const fetchUpdatedData = async () => {
            try {
                const result = await axios.post('http://127.0.0.1:5000/savings', {
                    balance: 300000,  // example values
                    interest_rate: 3.5,
                    monthly_payment: 1500,
                    extra_payment: parseFloat(extraPayment),
                    term: 360
                });
                setData(result.data.schedule); // Adjust according to the returned data structure
            } catch (err) {
                console.error(err);
                setError('Failed to fetch data from server');
            }
        };

        if (extraPayment > 0) {
            fetchUpdatedData();
        }
    }, [extraPayment]);

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <LineChart width={600} height={300} data={data}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="month" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey="balance" stroke="#8884d8" activeDot={{ r: 8 }} />
            <Line type="monotone" dataKey="principal" stroke="#82ca9d" />
            <Line type="monotone" dataKey="interest" stroke="#ff7300" />
        </LineChart>
    );
};

export default LoanChart;
