import React, { useEffect, useState } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';
import axios from 'axios';

const LoanChart = ({ extraPayment }) => {
    const [scheduleData, setScheduleData] = useState([]);
    const [scenarioData, setScenarioData] = useState([]);
    const [error, setError] = useState(null);

    const monthsToYears = (months) => Math.floor(months / 12);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const result = await axios.post('http://127.0.0.1:5000/schedule', {
                    balance: 300000,  // example values
                    interest_rate: 3.5,
                    monthly_payment: 1500,
                    term: 360
                });
                setScheduleData(result.data);
            } catch (err) {
                console.error(err);
                setError('Failed to fetch data from server');
            }
        };
        fetchData();
    }, []);

    useEffect(() => {
        const fetchUpdatedData = async () => {
            try {
                const result = await axios.post('http://127.0.0.1:5000/savings', {
                    balance: 300000,  // example values
                    interest_rate: 3.5,
                    monthly_payment: 1500,
                    extra_payment: parseFloat(extraPayment),
                    term: 360
                });
                setScenarioData(result.data.schedule);
            } catch (err) {
                console.error(err);
                setError('Failed to fetch data from server');
            }
        };

        if (extraPayment > 0) {
            fetchUpdatedData();
        } else {
            setScenarioData([]);
        }
    }, [extraPayment]);

    if (error) {
        return <div>Error: {error}</div>;
    }

    const formatTooltip = (value) => `$${value.toFixed(2)}`;

    return (
        <LineChart width={800} height={400} data={scheduleData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="month" tickFormatter={monthsToYears} label={{ value: 'Years', position: 'insideBottomRight', offset: -5 }} domain={[0, 'dataMax']} />
            <YAxis />
            <Tooltip formatter={formatTooltip} />
            <Legend />
            <Line type="monotone" dataKey="balance" name="Scheduled Balance" stroke="#8884d8" />
            {scenarioData.length > 0 && (
                <Line type="monotone" dataKey="balance" name="Scenario Balance" stroke="#82ca9d" />
            )}
        </LineChart>
    );
};

export default LoanChart;
