import React, { useState } from 'react';
import { TextField, Button, Typography } from '@mui/material';
import axios from 'axios';

const SavingsCalculator = () => {
    const [extraPayment, setExtraPayment] = useState(0);
    const [savings, setSavings] = useState(null);

    const calculateSavings = async () => {
        const result = await axios.post('http://127.0.0.1:5000/savings', {
            balance: 300000,  // example values
            interest_rate: 3.5,
            monthly_payment: 1500,
            extra_payment: parseFloat(extraPayment),
            term: 360
        });
        setSavings(result.data);
    };

    return (
        <div>
            <TextField
                label="Extra Monthly Payment"
                variant="outlined"
                value={extraPayment}
                onChange={(e) => setExtraPayment(e.target.value)}
                type="number"
            />
            <Button variant="contained" color="primary" onClick={calculateSavings}>
                Calculate Savings
            </Button>
            {savings && (
                <div>
                    <Typography variant="h6">Savings in Interest: ${savings.savings_interest.toFixed(2)}</Typography>
                    <Typography variant="h6">Savings in Time: {Math.floor(savings.savings_time_months / 12)} years and {savings.savings_time_months % 12} months</Typography>
                </div>
            )}
        </div>
    );
};

export default SavingsCalculator;
